export interface ISingleParamFuns {
    integer: (integer: string | number) => boolean;
    email: (email: string | number) => boolean;
    mobile: (mobile: string | number) => boolean;
    required: (value: any) => boolean;
    pan: (value: number | string) => boolean;
    aadhar: (value: number | string) => boolean;
    gstin: (value: number | string) => boolean;
    alphaNumeric: (value: number | string) => boolean;
    alphaNumericWithSpace: (value: number | string) => boolean;
    customHandler: (value: number | string) => boolean;
}
export interface IMultiParamFuns {
    regex: (value: string | number, regexString: string) => boolean;
    maxValue: (value: number | string, maxValue: number) => boolean;
    minValue: (value: number | string, minValue: number) => boolean;
    minLength: (value: number | string, minLength: number) => boolean;
    maxLength: (value: number | string, maxLength: number) => boolean;
    multipleOf: (value: number | string, multiple: number) => boolean;
}
